import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ProductEditComponent = class ProductEditComponent {
    constructor(productService, route, router) {
        this.productService = productService;
        this.route = route;
        this.router = router;
    }
    ngOnInit() {
        this.route.params.subscribe(params => {
            const id = params['id'];
            this.productService.getProduct(id).subscribe(productToEdit => this.product = productToEdit);
        });
    }
    async onSave(form) {
        this.product.name = form.value.name;
        this.product.description = form.value.description;
        this.product.price = form.value.price;
        this.product.quantity = form.value.quantity;
        this.productService.add(this.product).subscribe(() => this.router.navigate(['home']));
    }
};
ProductEditComponent = __decorate([
    Component({
        selector: 'app-product-edit',
        templateUrl: './product-edit.component.html',
        styleUrls: ['./product-edit.component.css']
    })
], ProductEditComponent);
export { ProductEditComponent };
